<script setup>
import VueDatePicker from '@vuepic/vue-datepicker';
import '@vuepic/vue-datepicker/dist/main.css';
import dayjs from 'dayjs';

definePageMeta({
    title: 'Dashboard',
});

const url = useRuntimeConfig().public.authUrl;
const authStore = useAuthStore();
const superadminDashboardStore = useSuperadminDashboardStore();

const loadingCards = ref(true);
const loadingBarChart = ref(true);
const loadingList = ref(true);
const loadingDonut = ref(true);
const pending = ref(false);

const now = new Date();

// CSS that you need to add to your stylesheet for hover effects

const chartOptions = ref({
    chart: {
        type: 'bar',
        height: 350,
        toolbar: { show: false },
        background: '#fff',
    },
    plotOptions: {
        bar: {
            horizontal: false,
            columnWidth: '45%', // Thinner bars
            endingShape: 'flat',
            borderRadius: 0,
            dataLabels: {
                position: 'top',
            },
        },
    },
    dataLabels: {
        enabled: false, // Disable data labels for cleaner look
    },
    xaxis: { categories: [] }, // default kosong
});

const series = ref([{ name: 'Total Borrowed', data: [] }]);

const getMajorLoansChart = async () => {
    loadingBarChart.value = true;
    const response = await $fetch(
        `${url}/dashboard/superadmin/major-loans?from=${superadminDashboardStore.filter.from}&to=${superadminDashboardStore.filter.to}`,
        {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${authStore.token}`,
            },
        }
    );

    if (response.status === 200) {
        loadingBarChart.value = false;
        superadminDashboardStore.majorLoans = response.data;
    }

    const categories = superadminDashboardStore.majorLoans.map((item) => item.major);
    const seriesData = superadminDashboardStore.majorLoans.map((item) => parseInt(item.count));

    chartOptions.value = {
        ...chartOptions.value, // merge biar ga hilang properti chart
        xaxis: {
            categories,
            labels: {
                style: { colors: '#666', fontSize: '12px', fontWeight: '500' },
            },
        },
        yaxis: {
            min: 0,
            max: Math.max(...seriesData) + 5,
            tickAmount: 6,
            labels: { style: { colors: '#666', fontSize: '12px' } },
        },
    };

    series.value = [{ name: 'Total Borrowed', data: seriesData }];
};

const getLatestActivty = async () => {
    pending.value = true;
    loadingList.value = true;
    const response = await $fetch(`${url}/dashboard/superadmin/items-loans-history`, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${authStore.token}`,
        },
    });

    if (response.status === 200) {
        pending.value = false;
        loadingList.value = false;
        superadminDashboardStore.latestActivityRecords = response.data;
        loadingLatestActivity.value = false;
    }
};

const getCardsData = async () => {
    loadingCards.value = true;
    const response = await $fetch(`${url}/dashboard/superadmin/card`, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${authStore.token}`,
        },
    });

    if (response.status === 200) {
        loadingCards.value = false;
        superadminDashboardStore.cardsData = response.data;
    }
};

const donutSeries = ref([]);
const donutOptions = ref({
    chart: {
        type: 'donut',
        toolbar: {
            show: false,
        },
    },
    labels: [], // akan diisi dari API
    colors: ['#6366F1', '#22C55E', '#F59E0B', '#EF4444', '#3B82F6'],
    legend: {
        position: 'bottom',
        fontSize: '14px',
        fontWeight: 500,
        labels: {
            colors: '#374151',
        },
        formatter: function (val, opts) {
            // ambil value dari series sesuai index
            const value = opts.w.globals.series[opts.seriesIndex];
            // bikin legend custom: label di kiri, jumlah di kanan
            return `<div style="display:flex; justify-content:space-between; width:180px; justify-items:center">
                <span>${val}</span>
                <span><b>${value}x</b></span>
              </div>`;
        },
    },
    dataLabels: {
        enabled: false,
        formatter: (val) => val.toFixed(1) + '%',
        style: {
            fontSize: '12px',
            fontWeight: 'bold',
            colors: ['#fff'],
        },
    },
    tooltip: {
        y: {
            formatter: (val) => `${val}x dipinjam`,
        },
    },
    stroke: {
        show: true,
        width: 2,
        colors: ['#fff'],
    },
    plotOptions: {
        pie: {
            donut: {
                size: '65%',
                labels: {
                    show: true,
                    total: {
                        show: true,
                        label: 'Total',
                        fontSize: '14px',
                        fontWeight: 600,
                        formatter: (w) => {
                            return w.globals.seriesTotals.reduce((a, b) => a + b, 0) + 'x';
                        },
                    },
                },
            },
        },
    },
});

const getMostBorrowed = async () => {
    try {
        loadingDonut.value = true;
        const response = await $fetch(`${url}/dashboard/superadmin/most-borrowed`, {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${authStore.token}`,
            },
        });

        if (response.status === 200) {
            loadingDonut.value = false;
            superadminDashboardStore.mostBorrowed = response.data;

            // mapping ke ApexCharts
            donutSeries.value = response.data.map((item) => item.total_borrowed);
            donutOptions.value = {
                ...donutOptions.value,
                labels: response.data.map((item) => item.name),
            };
        }
    } catch (err) {
        console.error('Error fetching most borrowed:', err);
    }
};

const formatDate = (date) => {
    if (!date) return null;
    const d = new Date(date);
    const year = d.getFullYear();
    const month = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
};

// hari ini
const chartStartDay = dayjs().startOf('year').format('YYYY-MM-DD');
const chartEndDay = dayjs().endOf('year').format('YYYY-MM-DD');

// reactive datepicker model
const fromDate = ref(chartStartDay);
const toDate = ref(chartEndDay);

// set langsung ke Pinia (biar default udah ada)
superadminDashboardStore.filter.from = formatDate(chartStartDay);
superadminDashboardStore.filter.to = formatDate(chartEndDay);

const updateFrom = (date) => {
    fromDate.value = date;
    superadminDashboardStore.filter.from = formatDate(date);
};

const updateTo = (date) => {
    toDate.value = date;
    superadminDashboardStore.filter.to = formatDate(date);
};
const options = {
    weekday: 'short', // Thu
    day: '2-digit', // 31
    month: 'long', // July
    year: 'numeric', // 2025
    hour: '2-digit',
    minute: '2-digit',
    hour12: true, // AM/PM
};

onMounted(() => {
    getCardsData();
    getMostBorrowed();
    getLatestActivty();
    watchEffect(() => {
        if (superadminDashboardStore.filter.from && superadminDashboardStore.filter.to) {
            getMajorLoansChart();
        }
    });
});

const formattedDate = new Intl.DateTimeFormat('en-US', options).format(now);
// contoh hasil asli: "Thu, July 31, 2025, 07:30 AM"

const parts = formattedDate.replace(',', '').split(' ');
// parts = ['Thu', 'July', '31', '2025', '07:30', 'AM']

const today = `${parts[0]}, ${parts[2]} ${parts[1]} ${parts[3]} - ${parts[4]} ${parts[5]}`;
</script>

